import React, { useEffect, useState, useRef } from 'react';
import './jsonbuilder.css';

var inputPos = { x: 0, y: 0 }
var inputOffset = { x: 0, y: 0 }
const JSONBuilder = props => {
	const [isDragging, setIsDragging] = useState(false);
  const [pos, setPos] = useState({ x: 0, y: 0 });
	const ref = useRef(null);
	
		function onMouseMove(e) {
			if (!isDragging) return;
			setPos({
				x: e.x - ref.current.offsetWidth / 2,
				y: e.y - ref.current.offsetHeight / 2,
			});
			e.stopPropagation();
			e.preventDefault();

			// console.log(setPos.x)
		}
	
		function onMouseUp(e) {
			setIsDragging(false);
			e.stopPropagation();
			e.preventDefault();
		}
	
		function onMouseDown(e) {
			if (e.button !== 0) return;
			setIsDragging(true);
	
			setPos({
				x: e.x - ref.current.offsetWidth / 2,
				y: e.y - ref.current.offsetHeight / 2,
			});
	
			e.stopPropagation();
			e.preventDefault();
			console.log(setPos.x)
		}
	
		// When the element mounts, attach an mousedown listener
		useEffect(() => {
			ref.current.addEventListener("mousedown", onMouseDown);
	
			return () => {
				ref.current.removeEventListener("mousedown", onMouseDown);
			};
		}, []);
	
		// Everytime the isDragging state changes, assign or remove
		// the corresponding mousemove and mouseup handlers
		useEffect(() => {
			if (isDragging) {
				document.addEventListener("mouseup", onMouseUp);
				document.addEventListener("mousemove", onMouseMove);
			} else {
				document.removeEventListener("mouseup", onMouseUp);
				document.removeEventListener("mousemove", onMouseMove);
			}
			return () => {
				document.removeEventListener("mouseup", onMouseUp);
				document.removeEventListener("mousemove", onMouseMove);
			};
		}, [isDragging]);

	return(
		<div className={ `JSON-Builder` } onMouseDown={ onMouseDown } ref={ ref }>

		</div>
	);
}

export default JSONBuilder;
