import React, { useRef } from 'react';
import './jsonbuilder.css';

var viewport = {
	height: window.innerHeight,
	width: window.innerWidth,
}

var startPos = { x: 0, y: 0 }
var dragOffset = { x: 0, y: 0 }
var hitBox = {
	top: {
		val: 0,
		drop: 0,
		hit: false,
	},
	right: {
		val: 0,
		drop: 0,
		hit: false,
	},
	bottom: {
		val: 0,
		drop: 0,
		hit: false,
	},
	left: {
		val: 0,
		drop: 0,
		hit: false,
	}
}

let logTick = true;
const logTimer = () => {
	logTick = false;
	setTimeout(()=>{
		logTick = true;
	}, 200);
}
const JSONBuilder = () => {
	const ref = useRef(null);

	function handleMouseMove(e) {
		let screenPos = e.target.getBoundingClientRect();
		hitBox.top.val = screenPos.top;
		hitBox.right.val = screenPos.right;
		hitBox.bottom.val = screenPos.bottom;
		hitBox.left.val = screenPos.left;



		// dragOffset.x = e.screenX - startPos.x;
			
		
// console.log(logTick)

		
		// hit detection top
		if ( hitBox.top.val >= 0 && !hitBox.top.hit ) {
			dragOffset.y = e.screenY - startPos.y;
		} else if ( !hitBox.top.hit ) {
			hitBox.top.hit = true;
			hitBox.top.drop = e.screenY;
		} else if ( e.screenY > hitBox.top.drop ) {
			hitBox.top.hit = false;
			hitBox.top.val = 1;
			dragOffset.y += 1;
		}

		// hit detection right
		if ( hitBox.right.val <= viewport.width && !hitBox.right.hit ) {
			dragOffset.x = e.screenX - startPos.x;
		} else if ( !hitBox.right.hit ) {
			hitBox.right.hit = true;
			hitBox.right.drop = e.screenX;
		} else if ( e.screenX > hitBox.right.drop ) {
			hitBox.right.hit = false;
			hitBox.right.val = viewport.width-1;
			dragOffset.x += 1;
		}

		logTick && console.log(e.screenX, hitBox.right.drop, dragOffset.x, hitBox.right.val, viewport.width, hitBox.right.hit);
    e.stopPropagation();
    e.preventDefault();
		e.target.style.transform = `translate3d( ${ dragOffset.x }px, ${ dragOffset.y }px,0 )`;
		// console.log(e.target.getBoundingClientRect())
		logTick && logTimer();
  }

  function handleMouseUp(e) {
    e.stopPropagation();
    e.preventDefault();
		e.target.removeEventListener( 'mousemove', handleMouseMove );
		window.removeEventListener( 'mouseup', handleMouseUp );
  }

  function handleMouseDown(e) {
		e.stopPropagation();
    e.preventDefault();
		e.target.addEventListener( 'mousemove', handleMouseMove );
		window.addEventListener( 'mouseup', handleMouseUp );
		startPos = {
			x: e.screenX - dragOffset.x,
			y: e.screenY - dragOffset.y,
		}
  }

	return(
		<div className={ `JSON-Builder` } onMouseDown={ handleMouseDown } ref={ ref }>

		</div>
	);
}

export default JSONBuilder;
