import React, { useRef } from 'react';
import './jsonbuilder.css';

var startPos = { x: 0, y: 0 }
var dragOffset = { x: 0, y: 0 }
const JSONBuilder = () => {
	const ref = useRef(null);

	function handleMouseMove(e) {
		let screenPos = e.target.getBoundingClientRect();
    dragOffset = {
			x: e.screenX - startPos.x,
			
		}
		if ( screenPos.top >= 0 ) {
			dragOffset.y = e.screenY - startPos.y;
		}
    e.stopPropagation();
    e.preventDefault();
		e.target.style.transform = `translate3d( ${ dragOffset.x }px, ${ dragOffset.y }px,0 )`;
		// console.log(e.target.getBoundingClientRect())
  }

  function handleMouseUp(e) {
    e.stopPropagation();
    e.preventDefault();
		e.target.removeEventListener( 'mousemove', handleMouseMove );
		window.removeEventListener( 'mouseup', handleMouseUp );
  }

  function handleMouseDown(e) {
		e.stopPropagation();
    e.preventDefault();
		e.target.addEventListener( 'mousemove', handleMouseMove );
		window.addEventListener( 'mouseup', handleMouseUp );
		startPos = {
			x: e.screenX - dragOffset.x,
			y: e.screenY - dragOffset.y,
		}
  }

	return(
		<div className={ `JSON-Builder` } onMouseDown={ handleMouseDown } ref={ ref }>

		</div>
	);
}

export default JSONBuilder;
