import React, { useRef } from 'react';
import './jsonbuilder.css';

var inputPos = { x: 0, y: 0 }
var inputOffset = { x: 0, y: 0 }
const JSONBuilder = () => {
	const ref = useRef(null);

	function handleMouseMove(e) {
    // if (!isDragging) return;
		
    inputOffset = {
			x: e.screenX - inputPos.x,
			y: e.screenY - inputPos.y,
		}
		console.log(inputOffset)
    e.stopPropagation();
    e.preventDefault();
		// e.style.transform = 'scale(2)';
		// setPos({
    //   x: e.screenX - inputPos.x,
		// 	y: e.screenY - inputPos.y,
    // });
		console.log(e.target)
  }

  function handleMouseUp(e) {
    setIsDragging(false);
    e.stopPropagation();
    e.preventDefault();
		e.target.removeEventListener( 'mousemove', handleMouseMove );
		e.target.removeEventListener( 'mouseup', handleMouseUp );
  }

  function handleMouseDown(e) {
		console.log(e)
		e.stopPropagation();
    e.preventDefault();
    if (e.button !== 0) return;
    setIsDragging(true);
		e.target.addEventListener( 'mousemove', handleMouseMove );
		e.target.addEventListener( 'mouseup', handleMouseUp );
		inputPos = {
			x: e.screenX,
			y: e.screenY,
		}
		
    // setPos({
    //   x: e.x - ref.current.offsetWidth / 2,
    //   y: e.y - ref.current.offsetHeight / 2,
    // });

    // e.stopPropagation();
    // e.preventDefault();
  }

	return(
		<div className={ `JSON-Builder` } onMouseDown={ handleMouseDown } ref={ ref }>

		</div>
	);
}

export default JSONBuilder;
